trait BSeller_SkyHub_Trait_Integration_Middlewares
{
    
    /**
     * @return BSeller_SkyHub_Model_Integrator_Middleware_Catalog_Product
     */
    public function catalogProductIntegratorMiddleware()
    {
        /** @var BSeller_SkyHub_Model_Integrator_Middleware_Catalog_Product $middleware */
