     * @return BSeller_SkyHub_Model_Integrator_Middleware_Catalog_Product_Attribute
     */
    public function catalogProductAttributeIntegratorMiddleware()
    {
        /** @var BSeller_SkyHub_Model_Integrator_Middleware_Catalog_Product_Attribute $middleware */
        $middleware = Mage::getSingleton('bseller_skyhub/integrator_middleware_catalog_product_attribute');
        return $middleware;
